import React, { useState, useEffect } from "react";

import userList from "./food";

import "./styles.css";

export const  Parse = ({}) =>  {
  const [users, setUsers] = useState([]);
  const [search, setSearch] = useState(null);
  const [category, setCategory] = useState(null);

  useEffect(() => {
    getUsers(userList);
  }, []);

  //Simulating making api call with useEffect
  const getUsers = userList => {
    setUsers(userList);
  };

  const byCategory = (user, category) => {
    if (category) {
      return user.category === category;
    } else return user;
  };
  const bySearch = (user, search) => {
    if (search) {
      return user.name.toLowerCase().includes(search.toLowerCase());
    } else return user;
  };

  const filteredList = (users, category, search) => {
    return users
      .filter(user => byCategory(user, category))
      .filter(user => bySearch(user, search));
  };

  return (
    <div className="App">
      <input
        type="search"
        placeholder="search..."
        onChange={e => setSearch(e.target.value)}
      />
      <select onChange={e => setCategory(e.target.value)}>
        <option value="">Choose Category</option>
        <option value="FoodClass">FoodClass</option>
        <option value="description">description</option>
        <option value="publicationData">publicationDate</option>
        <option value="dataType">dataType</option>
        <option value="foodCategory">foodCategory</option>
        <option value="foodDescription">foodDescription</option>
        <option value="id">id</option>
        <option value="code">code</option>
        <option value="fdcId">fdcId</option>{" "}
      </select>
      {filteredList(users, category, search).map(user => (
        <div key={user.id}>
          {user.name} : {user.category}
        </div>
      ))}
    </div>
  );
}
export default Parse;
